package com.training.operators;

public class AssignmentOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;  
		int b=20;  
		a+=4;//a=a+4 (a=10+4)  
		b-=4;//b=b-4 (b=20-4)  
		System.out.println(a);  
		System.out.println(b);  
		
		int a1=10;  
		a1+=3;//10+3  
		System.out.println(a1);  
		a1-=4;//13-4  
		System.out.println(a1);  
		a1*=2;//9*2  
		System.out.println(a1);  
		a1/=2;//18/2  
		System.out.println(a1);  
	}

}
