package com.training.operators;

public class TernaryOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=2;  
		int b=5;  
		int min=(a<b)?a:b;  
		System.out.println(min);  
		
		int c=10;  
		int d=5;  
		int min1=(c<d)?c:d;  
		System.out.println(min1);  
	}

}
